import { Component } from 'react';
import PropTypes from 'prop-types';

import MarvelService from '../../services/MarvelService';
import Spinner from '../spinner/Spinner';
import ErrorMessage from '../errorMessage/ErrorMessage';

import './charList.scss';

class CharList extends Component{
    state = {
        charList: [],
        loading: true,
        error: false,
        newItemsLoading: false,
        offset: 210,
        charListEnd: false,
    };

    marvelService = new MarvelService();

    componentDidMount() {
        this.onRequest(); //запускается и при первичной загрузке => аргумента нет и метод ориенируется на _baseOffset в MarvelService
    }

    onRequest = (offset) => {
        this.onCharListLoading();

        this.marvelService
            .getAllCharacters(offset)
            .then(this.onCharLoaded)
            .catch(this.onError)
    }

    onCharListLoading = () => {
        this.setState({
            newItemsLoading: true
        })
    }

    onCharLoaded = (newCharList) => {
        let end = false;
        if (newCharList.length < 9) {
            end = true;
        }

        this.setState( ({charList, offset}) => ({
            charList: [...charList, ...newCharList],  //развернула старый массив персонажей и за ним добавила новый. При первичной загрузке charlist - пустой массив
            loading: false,
            newItemsLoading: false,
            offset: offset + 9,
            charListEnd: end,
        }))
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true
        })
    }

    renderCharacters(arr) {
        const characters = arr.map( obj => {
            let imgStyle = {'objectFit' : 'cover'};
            if (obj.thumbnail.includes('image_not_available')) {
                imgStyle = {'objectFit' : 'unset'};
            }            


            //let classNaming = event.target.textContent === obj.name;
            // {classNaming ? 'char__item char__item_selected' : 'char__item'}
           //хуйня

           this.state.

           console.log(this.state.isChecked);
            return (
                <li className='char__item' key={obj.id} onClick={(event) => {
                    if (obj.name === event.target.textContent) {
                        this.setState({isChecked: !this.state.isChecked})
                        event.target.className = 'char__item  char__item_selected'
                    }
                    // console.log(obj.name);
                    // console.log(event.target.textContent);

                // if (this.props.charId !== obj.id){
                //     event.target.className = 'char__item char__item_selected';
                // }

                    // if (event.target.textContent === obj.name && event.target.tagName === 'li') {
                    //     event.target.className += ' char__item_selected'
                    // } else {
                    //     event.target.className = 'char__item'
                    // }
                    //почему-то тоже ничего не делает

                    // if (event.target.textContent === obj.name && event.target.tagName === 'li') {
                    //     event.target.className = 'char__item char__item_selected'
                    // } else {
                    //     event.target.className = 'char__item'
                    // } 
                    //ничего не делает

                    this.props.onCharSelected(obj.id)
                }}>
                    <img src={obj.thumbnail} alt={obj.name}  style={imgStyle}/>
                    <div className="char__name">{obj.name}</div>
                </li>
            ) //  УБРАТЬ АКТИВНЫЙ СТИЛЬ - безз него класс char__item

        });

        return (
            <ul className="char__grid">
                {characters}
            </ul>

            // <ul className="char__grid" onClick={ event => {
            //     if (event.target.tagName === 'LI') {
            //         event.target.className = 'char__item char__item_selected';
            //     }

            // }}>
            //     {characters}
            // </ul>
        ) //Для центровки спиннера/ошибки
    }


    render() {
        const {charList, loading, error, newItemsLoading, offset, charListEnd} = this.state;

        const items = this.renderCharacters(charList);

        const errorMessage = error ? <ErrorMessage/> : null;
        const spinner = loading ? <Spinner/> : null;
        const content = !loading && !error ? items : null;

        return (
            <div className="char__list">
                    {content}
                    {spinner}
                    {errorMessage}
                <button
                className="button button__main button__long"
                disabled={newItemsLoading}
                style={{'display': charListEnd ? 'none' : 'block'}}
                onClick={() => {this.onRequest(offset)}}>
                    <div className="inner">load more</div>
                </button>
            </div>
        )
    }

}

CharList.propTypes = {
    onCharSelected: PropTypes.func.isRequired
}

export default CharList;
